package com.phodal.agenttrace.model;

import com.fasterxml.jackson.annotation.JsonValue;

/**
 * Types of contributors in Agent Trace.
 * 
 * @see <a href="https://agent-trace.dev/">Agent Trace Specification</a>
 */
public enum ContributorType {
    /**
     * Code authored directly by a human developer
     */
    HUMAN("human"),
    
    /**
     * Code generated by AI
     */
    AI("ai"),
    
    /**
     * Human-edited AI output or AI-edited human code
     */
    MIXED("mixed"),
    
    /**
     * Origin cannot be determined
     */
    UNKNOWN("unknown");

    private final String value;

    ContributorType(String value) {
        this.value = value;
    }

    @JsonValue
    public String getValue() {
        return value;
    }

    public static ContributorType fromValue(String value) {
        for (ContributorType type : values()) {
            if (type.value.equalsIgnoreCase(value)) {
                return type;
            }
        }
        return UNKNOWN;
    }
}
